    public Connection setKoneksi(){
        try {
            Class.forName("com.mysql.jdbc.Driver");
            conn=DriverManager.getConnection("jdbc:mysql://localhost/pkp","root","");
            stm=conn.createStatement();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,"Koneksi Gagal :" +e);
        }
       return conn; 
    }
    public void tabel(){
        Object header[]={"ID PENYEWAAN","ID PENYEWA","NAMA PENYEWA","TELEPON","ALAMAT","NAMA FASILITAS","TANGGAL","HARGA","TOTAL"};
        DefaultTableModel data=new DefaultTableModel(null,header);
        tabel.setModel(data);
        setKoneksi();
        String sql="SELECT * FROM pemesanan";
        try {
            ResultSet rs=stm.executeQuery(sql);
            while (rs.next())
            {
                String kolom1=rs.getString("idpesanan");
                String kolom2=rs.getString("id_penyewa");
                String kolom3=rs.getString("nama");
                String kolom4=rs.getString("telepon");
                String kolom5=rs.getString("alamat"); 
                String kolom6=rs.getString("nama_fasilitas");
                String kolom7=rs.getString("tanggal");
                String kolom8=rs.getString("harga");
                String kolom9=rs.getString("total");
                
                String kolom[]={kolom1,kolom2,kolom3,kolom4,kolom5,kolom6,kolom7,kolom8,kolom9};
                data.addRow(kolom);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
