    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed
    String sql = "insert into transaksi values (?,?,?,?,?,?,?)";
            try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, tgl.getText());
            stat.setString(2, idpemesanan.getText());
            stat.setString(3, id.getText());
            stat.setString(4, fasilitas.getText());
            stat.setString(5, total.getText());
            stat.setString(6, bayar.getText());
            stat.setString(7, kembali.getText());
           

            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data akan disimpan");     
            kosong();
            tabel2();
            }
            catch (SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal disimpan"+e);
            }
    }//GEN-LAST:event_saveActionPerformed

