    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String reportSource = null;
        String reportDest = null;
        HashMap <String,Object> parameter = new HashMap <String,Object>();
        String response;
        response = JOptionPane.showInputDialog("Masukkan ID");  
        try{
           parameter.put("id", response);
         
             Class.forName("com.mysql.jdbc.Driver");
            conn =DriverManager.getConnection("jdbc:mysql://localhost:3306/pkp","root","");
            reportSource = System.getProperty("user.dir") + "/report/struk.jrxml";
            reportDest = System.getProperty("user.dir") + "/report/struk.jasper";
            
            JasperReport jasperReport = JasperCompileManager.compileReport(reportSource);
            JasperPrint jasperPrint = JasperFillManager.fillReport(jasperReport,parameter,conn);
            JasperExportManager.exportReportToHtmlFile(jasperPrint, reportDest);
            JasperViewer.viewReport(jasperPrint,false);
            
        }catch(Exception e){
            System.out.println(e);
        }
    }//GEN-LAST:event_jButton1ActionPerformed
public Connection setKoneksii(){
        try {
            Class.forName("com.mysql.jdbc.Driver");
            conn=DriverManager.getConnection("jdbc:mysql://localhost/pkp","root","");
            stm=conn.createStatement();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null,"Koneksi Gagal :" +e);
        }
       return conn; 
    }
    
    public void tabel2(){
        Object header[]={"TANGGAL","ID TRANSAKSI","ID PENYEWA","FASILITAS","TOTAL","BAYAR","KEMBALI"};
        DefaultTableModel data=new DefaultTableModel(null,header);
        tabel2.setModel(data);
        setKoneksi();
        String sql="select*from transaksi";
        try {
            ResultSet rs=stm.executeQuery(sql);
            while (rs.next())
            {
                String kolom1=rs.getString("tanggal");
                String kolom2=rs.getString("idpemesanan");
                String kolom3=rs.getString("idpenyewa");
                String kolom4=rs.getString("fasilitas");
                String kolom5=rs.getString("total");
                String kolom6=rs.getString("bayar");
                String kolom7=rs.getString("kembali");
                
                String kolom[]={kolom1,kolom2,kolom3,kolom4,kolom5,kolom6,kolom7};
                data.addRow(kolom);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "data gagal dipanggil"+e);
        }
    }
